import React, { Fragment, useEffect, useState } from 'react';
import Header from '../components/header';
import Question from '../components/question';
import Description from '../components/description';
import LeftSide from '../components/leftside';
import Rightside from '../components/rightside';

const Quiz = () => {
    const [levelId, setLevelId] = useState(1);

    const nextPage = () => {
        setCourseId(levelId + 1);
    };

    const prevPage = () => {
        setCourseId(levelId - 1)
    }

    useEffect(() => {
        fetch(`https://adeo.app/api/questions/get?level_id=${levelId}&course_id=1&limit=20`)
    }, [])
    return(
        <Fragment>
            <Header />
            <Question text={'Which of the following is a breed of rabbit?'} />
            <Description text={'Choose the right answer to the question above'} />
            <div className='main-content'>
                <LeftSide />
                <Rightside />
            </div>
        </Fragment>
    )
}

export default Quiz;
