import React, { Fragment, useEffect, useState } from 'react';
import Header from '../components/header';
import Question from '../components/question';
import Description from '../components/description';
import LeftSide from '../components/leftside';
import Rightside from '../components/rightside';

const Quiz = () => {
    const [error, setError] = useState(null);
    const [levelId, setLevelId] = useState(1);
    const [isLoaded, setIsLoaded] = useState(false);
    const [items, setItems] = useState([]);

    const nextPage = () => {
        setLevelId(levelId + 1);
    };

    const prevPage = () => {
        setLevelId(levelId - 1)
    }

    useEffect(() => {
        fetch(`https://adeo.app/api/questions/get?level_id=${levelId}&course_id=1&limit=20`)
        .then(res => res.json())
        .then(
            (result) => {
                setIsLoaded(true);
                setItems(result);
            },
            (error) => {
                setIsLoaded(true);
                setError(error);
            }
        )
    }, [])

    if (error) {
        return <div>Error: {error.message}</div>;
    }
    else if (!isLoaded) {
        return <div>Loading...</div>;
    }
    else {
        return(

            // <Fragment>
            //     {items.map(item => (
            //         <Fragment key={item}>

            //         </Fragment>
            //     ))}
            //     <Header />
            //     <Question text={'Which of the following is a breed of rabbit?'} />
            //     <Description text={'Choose the right answer to the question above'} />
            //     <div className='main-content'>
            //         <LeftSide />
            //         <Rightside />
            //     </div>
            // </Fragment>
            console.log(items && items)
       )
    }
}

export default Quiz;
